import React, { useState } from 'react';
import { api } from '../services/api';

interface FormData {
  username: string;
  email: string;
  telegramUsername: string;
}

interface FormErrors {
  username?: string;
  email?: string;
  telegramUsername?: string;
  general?: string;
}

const ReminderForm: React.FC = () => {
  const [formData, setFormData] = useState<FormData>({
    username: '',
    email: '',
    telegramUsername: ''
  });
  const [errors, setErrors] = useState<FormErrors>({});
  const [isLoading, setIsLoading] = useState(false);
  const [isSuccess, setIsSuccess] = useState(false);
  const [usernamePattern, setUsernamePattern] = useState('');

  const generatePattern = (username: string): string => {
    if (username.length < 7) return '';
    return `${username.substring(0, 4)}..${username.substring(username.length - 3)}`;
  };

  const validateForm = (): boolean => {
    const newErrors: FormErrors = {};

    if (!formData.username.trim()) {
      newErrors.username = 'Username is required';
    } else if (formData.username.length < 7) {
      newErrors.username = 'Username must be at least 7 characters long';
    } else if (formData.username.length > 50) {
      newErrors.username = 'Username must be less than 50 characters';
    }

    if (!formData.email.trim()) {
      newErrors.email = 'Email is required';
    } else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) {
      newErrors.email = 'Please enter a valid email address';
    }

    // Validate Telegram username (optional but if provided, must be valid)
    if (formData.telegramUsername.trim()) {
      if (!/^[a-zA-Z0-9_]{5,32}$/.test(formData.telegramUsername.replace('@', ''))) {
        newErrors.telegramUsername = 'Telegram username must be 5-32 characters (letters, numbers, underscore only)';
      }
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleInputChange = (field: keyof FormData, value: string) => {
    setFormData(prev => ({ ...prev, [field]: value }));

    // Update username pattern preview
    if (field === 'username') {
      setUsernamePattern(generatePattern(value));
    }

    // Clear errors when user starts typing
    if (errors[field]) {
      setErrors(prev => ({ ...prev, [field]: undefined }));
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!validateForm()) return;

    setIsLoading(true);
    setErrors({});

    try {
      // Prepare submission data
      const submissionData = {
        username: formData.username,
        email: formData.email.toLowerCase(),
        telegramUsername: formData.telegramUsername.trim() 
          ? formData.telegramUsername.replace('@', '') 
          : undefined
      };

      const response = await api.registerUser(submissionData);

      if (response.success) {
        setIsSuccess(true);
        setFormData({ username: '', email: '', telegramUsername: '' });
        setUsernamePattern('');

        // Show success for 5 seconds then reset
        setTimeout(() => setIsSuccess(false), 5000);
      }
    } catch (error: any) {
      setErrors({
        general: error.response?.data?.error || 'Registration failed. Please try again.'
      });
    } finally {
      setIsLoading(false);
    }
  };

  if (isSuccess) {
    return (
      <div className="container">
        <div className="card fade-in">
          <div className="success-message">
            <h2>‚úÖ Registration Successful!</h2>
            <p>Welcome to PrismaX AI Reminder Service!</p>
            <p>You'll receive email notifications when it's your turn in the queue.</p>
            <p><strong>Your queue pattern:</strong> <code>{usernamePattern}</code></p>
            {formData.telegramUsername && (
              <div className="telegram-next-steps">
                <p>üì± <strong>Next step for Telegram notifications:</strong></p>
                <p>Message <strong>@PrismaXBot</strong> on Telegram to activate instant notifications!</p>
              </div>
            )}
            <button 
              onClick={() => setIsSuccess(false)}
              className="btn btn-primary"
            >
              Register Another User
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container">
      <div className="card fade-in">
        <div className="card-header">
          <h1>üéØ Join PrismaX AI Queue Notifications</h1>
          <p>Get notified when it's your turn to teleoperate the robotic arm!</p>
        </div>

      <form onSubmit={handleSubmit} className="registration-form">
        <div className="form-group">
          <label htmlFor="username">
            PrismaX AI Username *
          </label>
          <input
            type="text"
            id="username"
            value={formData.username}
            onChange={(e) => handleInputChange('username', e.target.value)}
            placeholder="Enter your PrismaX AI username"
            className={errors.username ? 'error' : ''}
            disabled={isLoading}
            minLength={7}
            maxLength={50}
          />
          {errors.username && <span className="error-text">{errors.username}</span>}
          {usernamePattern && (
            <div className="pattern-preview">
              <small>Queue pattern: <code>{usernamePattern}</code></small>
            </div>
          )}
        </div>

        <div className="form-group">
          <label htmlFor="email">
            Email Address *
          </label>
          <input
            type="email"
            id="email"
            value={formData.email}
            onChange={(e) => handleInputChange('email', e.target.value)}
            placeholder="Enter your email address"
            className={errors.email ? 'error' : ''}
            disabled={isLoading}
          />
          {errors.email && <span className="error-text">{errors.email}</span>}
        </div>

        <div className="form-group">
          <label htmlFor="telegramUsername">
            Telegram Username (Optional)
          </label>
          <input
            type="text"
            id="telegramUsername"
            value={formData.telegramUsername}
            onChange={(e) => handleInputChange('telegramUsername', e.target.value)}
            placeholder="@yourusername (optional)"
            className={errors.telegramUsername ? 'error' : ''}
            disabled={isLoading}
            maxLength={33}
          />
          {errors.telegramUsername && <span className="error-text">{errors.telegramUsername}</span>}
          <div className="telegram-info">
            <small>
              üì± Get instant Telegram notifications! After registering, message our bot 
              <strong> @PrismaXBot</strong> to activate notifications.
            </small>
          </div>
        </div>



        {errors.general && (
          <div className="general-error">
            {errors.general}
          </div>
        )}

        <div className="form-actions">
          <button 
            type="submit" 
            className="btn btn-primary"
            disabled={isLoading}
          >
            {isLoading ? 'Registering...' : 'üöÄ Start Monitoring Queue'}
          </button>
        </div>

        <div className="info-section">
          <h3>How it works:</h3>
          <ul>
            <li>üìù Register with your PrismaX AI username and email</li>
            <li>ÔøΩ Optionally add your Telegram username for instant notifications</li>
            <li>ÔøΩüîç Our system monitors the queue in real-time</li>
            <li>üìß Get instant email notification when it's your turn</li>
            <li>üöÄ Telegram users get 5 successive notifications over 2 minutes!</li>
            <li>‚ö° Never miss your robotic arm session again!</li>
          </ul>

          <div className="telegram-section">
            <h4>üì± Telegram Bot Setup:</h4>
            <p>For instant Telegram notifications:</p>
            <ol>
              <li>Add your Telegram username above (optional)</li>
              <li>After registering, find <strong>@PrismaXBot</strong> on Telegram</li>
              <li>Send any message to activate notifications</li>
              <li>Receive 5 instant alerts when it's your turn!</li>
            </ol>
          </div>
        </div>
      </form>
      </div>
    </div>
  );
};

export default ReminderForm;
