import React, { useEffect, useState } from 'react';
import { fetchQueueStatus } from '../services/api';

const QueueStatus: React.FC = () => {
    const [queueStatus, setQueueStatus] = useState<{ nextUser: string | null }>({ nextUser: null });

    useEffect(() => {
        const getQueueStatus = async () => {
            const status = await fetchQueueStatus();
            setQueueStatus(status);
        };

        getQueueStatus();
    }, []);

    return (
        <div>
            <h2>Queue Status</h2>
            {queueStatus.nextUser ? (
                <p>Next user in line: {queueStatus.nextUser}</p>
            ) : (
                <p>No users in the queue.</p>
            )}
        </div>
    );
};

export default QueueStatus;
